RULES_ITEM.SQL_TEXT:
declare
    v_task_date date;
    v_exp_date date;
    v_parent_id number;
begin

    for rec in (
        select xitor_id
          from xitor
         where xitor_type_id = id.permits.tt
    ) loop
    
        v_exp_date := util.getvaldatebyid(rec.xitor_id, id.permits.cf.prm_expiration_date);
        if v_exp_date is null then
            util.setvalnumbyid(rec.xitor_id, id.permits.cf.prm_permit_expired, 0);
            util.setvalnumbyid(rec.xitor_id, 1000066422 /*id.permits.cf.prm_days_to_expiration*/, null);
            continue;
        end if;
    
        v_parent_id := util.getparentid(rec.xitor_id, 'Project');
    
        begin
            select finish_actual_date
              into v_task_date
              from wp_tasks
             where xitor_id = v_parent_id
               and order_number = 7000 
               and rownum = 1;
        exception when no_data_found then
            v_task_date := null;
        end;

        if v_task_date is not null then
            util.setvalnumbyid(rec.xitor_id, id.permits.cf.prm_permit_expired, 0);
            util.setvalnumbyid(rec.xitor_id, 1000066422 /*id.permits.cf.prm_days_to_expiration*/, 0);
            continue;
        end if;
    
        if v_exp_date > current_date then
            util.setvalnumbyid(rec.xitor_id, id.permits.cf.prm_permit_expired, 0);
            util.setvalnumbyid(rec.xitor_id, 1000066422 /*id.permits.cf.prm_days_to_expiration*/, round(v_exp_date - current_date));
        else
            util.setvalnumbyid(rec.xitor_id, id.permits.cf.prm_permit_expired, 1);
            util.setvalnumbyid(rec.xitor_id, 1000066422 /*id.permits.cf.prm_days_to_expiration*/, 0);
        end if;
    
    end loop;  

end;

declare
    v_xitor_id number;
    v_300_af date;
    v_500_af date;
    v_wp_id number;
begin

    for rec in (
        select xitor_id
          from xitor
         where xitor_type_id = id.permits.tt
    ) loop
        
        v_xitor_id := rec.xitor_id;
        begin
            select wp_workplan_id
              into v_wp_id
              from wp_workplan
             where active = 1
               and xitor_id = v_xitor_id
               and template_workplan_id = 100128815 /*Permit Workplan*/
               and rownum = 1;
        exception when no_data_found then
            util.setvalnumbyid(v_xitor_id, 1000067268 /*id.permits.cf.prm_permit_age*/, null);
            continue;
        end;
    
        select finish_actual_date
          into v_300_af
          from wp_tasks
         where wp_workplan_id = v_wp_id
           and order_number = 300;

        select finish_actual_date
          into v_500_af
          from wp_tasks
         where wp_workplan_id = v_wp_id
           and order_number = 500;

        if v_300_af is null 
        or v_500_af is not null
        or util.getvalnumbyid(v_xitor_id, id.permits.cf.prm_permit_status) in (id.vt.v_prm_permit_status.cancelled, 10025442 /*Rejected*/) 
        then
            util.setvalnumbyid(v_xitor_id, 1000067268 /*id.permits.cf.prm_permit_age*/, 0);
        else
            util.setvalnumbyid(v_xitor_id, 1000067268 /*id.permits.cf.prm_permit_age*/, round(current_date - v_300_af));
        end if;

    end loop;

end;

declare
    v_xitor_id number;
    v_500_pf date;
    v_500_af date;
    v_300_af date;
    v_wp_id number;
begin

    for rec in (
        select xitor_id
          from xitor
         where xitor_type_id = id.permits.tt
    ) loop
        
        v_xitor_id := rec.xitor_id;
        begin
            select wp_workplan_id
              into v_wp_id
              from wp_workplan
             where active = 1
               and xitor_id = v_xitor_id
               and template_workplan_id = 100128815 /*Permit Workplan*/
               and rownum = 1;
        exception when no_data_found then
            util.setvalnumbyid(v_xitor_id, 1000067269 /*id.permits.cf.prm_permit_countdown*/, null);
            util.setvalnumbyid(v_xitor_id, 1000067267 /*id.permits.cf.prm_permit_expected*/, null, 1);
            continue;
        end;
        
        select finish_actual_date
          into v_300_af
          from wp_tasks
         where wp_workplan_id = v_wp_id
           and order_number = 300;
        
        if util.getvalnumbyid(v_xitor_id, id.permits.cf.prm_permit_status) in (id.vt.v_prm_permit_status.cancelled, 10025442 /*Rejected*/)
        or v_300_af is null 
        then
            util.setvalnumbyid(v_xitor_id, 1000067269 /*id.permits.cf.prm_permit_countdown*/, null);
            util.setvalnumbyid(v_xitor_id, 1000067267 /*id.permits.cf.prm_permit_expected*/, null, 1);
            continue;
        end if;
        
        select finish_projected_date,
               finish_actual_date
          into v_500_pf,
               v_500_af
          from wp_tasks
         where wp_workplan_id = v_wp_id
           and order_number = 500;

        if v_500_af is not null then
            util.setvalnumbyid(v_xitor_id, 1000067269 /*id.permits.cf.prm_permit_countdown*/, 0);
        else
            util.setvalnumbyid(v_xitor_id, 1000067269 /*id.permits.cf.prm_permit_countdown*/, round(v_500_pf - current_date));
        end if;
    
    end loop;
    
end;
